package nodebox.node;import org.junit.Test;import static junit.framework.TestCase.*;public class TypeConversionsTest {    @Test    public void testCanConvert() {        assertTrue(TypeConversions.canBeConverted(Port.TYPE_BOOLEAN, Port.TYPE_STRING));        assertTrue(TypeConversions.canBeConverted(Port.TYPE_STRING, Port.TYPE_BOOLEAN));        assertFalse(TypeConversions.canBeConverted(Port.TYPE_STRING, Port.TYPE_GEOMETRY));    }}